import React from 'react';
import PropTypes from "prop-types";
import classnames from 'classnames';
import { EuiDataGridColumnResizer } from './data_grid_column_resizer';

var EuiDataGridHeaderRow = props => {
  const {
    columns,
    columnWidths,
    className,
    setColumnWidth,
    'data-test-subj': _dataTestSubj,
    ...rest
  } = props;
  const classes = classnames('euiDataGridHeader', className);
  const dataTestSubj = classnames('dataGridHeader', _dataTestSubj);
  return <div role="row" className={classes} data-test-subj={dataTestSubj} {...rest}>
      {columns.map(props => {
      const {
        id
      } = props;
      const width = columnWidths[id];
      return <div role="columnheader" key={id} className="euiDataGridHeaderCell" data-test-subj={`dataGridHeaderCell-${id}`} style={{
        width: `${width}px`
      }}>
            {width ? <EuiDataGridColumnResizer columnId={id} columnWidth={width} setColumnWidth={setColumnWidth} /> : null}

            <div className="euiDataGridHeaderCell__content">{id}</div>
          </div>;
    })}
    </div>;
};

EuiDataGridHeaderRow.propTypes = {
  className: PropTypes.string,
  "aria-label": PropTypes.string,
  "data-test-subj": PropTypes.string,
  columns: PropTypes.arrayOf(PropTypes.shape({
    id: PropTypes.string.isRequired
  }).isRequired).isRequired,
  columnWidths: PropTypes.shape({}).isRequired,
  setColumnWidth: PropTypes.func.isRequired
};
export { EuiDataGridHeaderRow };
